var Snaps = require('./lib/snaps').Snaps;
var fs = require('fs');

(new Snaps('carlo_testing', 'testingtesting2')).then(function(snaps) {
  var file = fs.createReadStream('blah.jpg');
  return snaps.send(file, ['jcfrancisco'], 5);
}).then(function() {
  console.log("done");
}).catch(function(err) {
  console.log(err.stack);
});

